mapper.select:
SELECT TB_DIARY.DIARY_ID, TB_DIARY.MEMBER_ID, TB_DIARY.TITLE, TB_DIARY.CONTENTS, TB_DIARY.SECRET, TB_DIARY.REG_DT, TB_DIARY.UPD_DT, TB_MEMBER.NICKNAME, TB_MEMBER.PICTURE FROM TB_DIARY JOIN TB_MEMBER ON TB_DIARY.MEMBER_ID = TB_MEMBER.MEMBER_ID WHERE TB_DIARY.MEMBER_ID IN
#{following}
AND TB_DIARY.DIARY_ID < #{cursorPageable.cursor}
AND TB_DIARY.SECRET = 0 ORDER BY TB_DIARY.DIARY_ID DESC LIMIT #{cursorPageable.pageSize}

SELECT TB_DIARY_COMMENT.DIARY_COMMENT_ID, TB_DIARY_COMMENT.DIARY_ID, TB_DIARY_COMMENT.MEMBER_ID, TB_MEMBER.NICKNAME, TB_MEMBER.PICTURE, TB_DIARY_COMMENT.CONTENTS, TB_DIARY_COMMENT.REG_DT FROM TB_DIARY_COMMENT INNER JOIN TB_MEMBER ON TB_DIARY_COMMENT.MEMBER_ID = TB_MEMBER.MEMBER_ID WHERE DEL_AT = 'N' AND DIARY_ID IN
${id}
ORDER BY REG_DT DESC

mapper.update:
UPDATE TB_DIARY_COMMENT
    SET DEL_AT = #{delAt},
        UPD_ID = #{updId},
        UPD_DT = #{updDt}
WHERE
    DIARY_COMMENT_ID = #{diaryCommentId}
